import React from 'react';
import './App.css';

const numbers = [1, 2, 3];

class App extends React.Component {
  render() {
    return (
      <div className="centered">
        <div id="quote-box">
          <blockquote className="blockquote text-right">
            <i className="fas fa-quote-right"></i><p className="mb-0" id="text">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Integer posuere erat a ante.</p>
            <footer className="blockquote-footer">Someone famous in <cite title="Source Title" id="author">Source Title</cite></footer>
          </blockquote>
        </div>
        <Buttons />

    </div>
    );
  }
}

class Buttons extends React.Component {
  constructor(props) {
    super(props);
    this.handleClick= this.handleClick.bind(this);
  }

  handleClick =()=> {
    console.log("CLICKED");
  }
  render() {
    return(
      <div className="buttons">
          <a className="btn btn-info" id="tweet-quote" title="Tweet this quote!" target="_blank">
            <i className="fa fa-twitter"></i>
          </a>
          <a className="btn btn-info" id="tumblr-quote" title="Post this quote on tumblr!" target="_blank">
            <i className="fa fa-tumblr"></i>
          </a>
          <button 
            type="button" 
            className="btn btn-info" 
            id="new-quote"
            onClick={this.handleClick}>New quote</button>
      </div>
      );
  }
}

export default App;
