import React from 'react';
import './App.css';

const texts = [
  'Text 1',
  'Text 2',
  'Text 3',
  'Text 4'
];

class App extends React.Component {
  render() {
    return (
      <div className="centered">
        <div id="quote-box">
          <blockquote className="blockquote text-right">
            <i className="fas fa-quote-right"></i><p className="mb-0" id="text">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Integer posuere erat a ante.</p>
            <footer className="blockquote-footer">Someone famous in <cite title="Source Title" id="author">{numbers[0]}</cite></footer>
          </blockquote>
        </div>
        <Buttons />
      </div>
    );
  }
}

class Buttons extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      clickedText: ''
    }

    this.handleClick= this.handleClick.bind(this);
  }

    handleClick = (i) => {
        this.setState({ clickedText: texts[i] });
  };
  render() {
    const { clickedText } = this.state;
    return(
      <div className="buttons">
          <a className="btn btn-info" href="twitter.com/intent/tweet" id="tweet-quote" title="Tweet this quote!" target="_blank">
            <i className="fa fa-twitter"></i>
          </a>
          <a className="btn btn-info" id="tumblr-quote" title="Post this quote on tumblr!" target="_blank">
            <i className="fa fa-tumblr"></i>
          </a>
          <button 
            type="button" 
            className="btn btn-info" 
            id="new-quote"
            key={i}
            onClick={() => this.handleClick(i)}>New quote</button>
            <footer className="blockquote-footer">Someone famous in <cite title="Source Title" id="author">{this.state.item}</cite></footer>
      </div>
      );
  }
}

export default App;
