import React from 'react';
import './App.css';
//import Quotes from './components/quotes';


class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      quotes: [],
      quote: "",
      author: ""
    }

    this.handleClick= this.handleClick.bind(this);
    }

  handleClick = () => {
    const rand = 1 + Math.random() * (100 - 1);
    this.setState({ 
      quote: quotes.quotes[rand].quote,
      author: quotes.quotes[rand].author
     });
  }

  componentDidMount() {
    fetch('https://gist.githubusercontent.com/camperbot/5a022b72e96c4c9585c32bf6a75f62d9/raw/e3c6895ce42069f0ee7e991229064f167fe8ccdc/quotes.json')
      .then(res=> res.json())
      .then((data) => {
        console.log(data);
        this.setState({
          quotes: data,
          })
      })
      .catch(console.log)
  }

  render() {
    return (
      //<Quotes quotes={this.state.quote} />
      <div className="centered">
        <div id="quote-box">
          <blockquote className="blockquote text-right">
            <i className="fas fa-quote-right"></i><p className="mb-0" id="text">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Integer posuere erat a ante.</p>
            <footer className="blockquote-footer">Someone famous in <cite title="Source Title" id="author">{this.state.quote}</cite></footer>
          </blockquote>
          <div className="buttons">
            <a className="btn btn-info" href="twitter.com/intent/tweet" id="tweet-quote" title="Tweet this quote!" target="_blank">
              <i className="fa fa-twitter"></i>
            </a>
            <a className="btn btn-info" id="tumblr-quote" title="Post this quote on tumblr!" target="_blank">
              <i className="fa fa-tumblr"></i>
            </a>
            <button 
              type="button" 
              className="btn btn-info" 
              id="new-quote">New quote</button>
          </div>
        </div> 
      </div>
    );
  }
}
//data[1]["name"] quote: data.quotes[1].author
export default App;
